mapper.select:

select 

from x_clie_ventas_cambios_precio_tbl
where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and ID_CAMBIO = #{idCambio,jdbcType=BIGINT}
  and ID_CLIE_ALBARAN = #{idClieAlbaran,jdbcType=BIGINT}
  and LOCATOR_ID = #{locatorId,jdbcType=VARCHAR}
  and LINEA = #{linea,jdbcType=INTEGER}

mapper.delete:

delete from x_clie_ventas_cambios_precio_tbl
where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and ID_CAMBIO = #{idCambio,jdbcType=BIGINT}
  and ID_CLIE_ALBARAN = #{idClieAlbaran,jdbcType=BIGINT}
  and LOCATOR_ID = #{locatorId,jdbcType=VARCHAR}
  and LINEA = #{linea,jdbcType=INTEGER}

