mapper.select:
select 

from X_INTERFACES_ERR_CAB_TBL
where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and UID_ERROR = #{uidError,jdbcType=VARCHAR}

SELECT distinct ID_CLASE 
FROM X_INTERFACES_ERR_CAB_TBL
WHERE  UID_ACTIVIDAD = #{uidActividad, jdbcType=VARCHAR}

mapper.delete:
delete from X_INTERFACES_ERR_CAB_TBL
where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and UID_ERROR = #{uidError,jdbcType=VARCHAR}

delete from X_INTERFACES_ERR_DET_TBL
where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and UID_ERROR = #{uidError,jdbcType=VARCHAR}
