mapper.select:
select 

from x_auditorias_tbl
where UID_INSTANCIA = #{uidInstancia,jdbcType=VARCHAR}
  and UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and UID_TICKET_AUDITORIA = #{uidTicketAuditoria,jdbcType=VARCHAR}

mapper.delete:
delete from x_auditorias_tbl
where UID_INSTANCIA = #{uidInstancia,jdbcType=VARCHAR}
  and UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and UID_TICKET_AUDITORIA = #{uidTicketAuditoria,jdbcType=VARCHAR}
