mapper.select:
select 

from D_CAJA_CAB_TBL
where UID_ACTIVIDAD = #{activityId,jdbcType=VARCHAR}
  and UID_DIARIO_CAJA = #{cashJournalUid,jdbcType=VARCHAR}

mapper.update:
update D_CAJA_CAB_TBL

    FECHA_CIERRE = #{closingDate,jdbcType=DATE},
    USUARIO_CIERRE = #{closeUserCode,jdbcType=VARCHAR},

where UID_ACTIVIDAD = #{activityId,jdbcType=VARCHAR}
  and UID_DIARIO_CAJA = #{cashJournalUid,jdbcType=VARCHAR}
  and FECHA_CIERRE IS NULL

update D_CAJA_CAB_TBL

    FECHA_CIERRE = #{closingDate,jdbcType=TIMESTAMP},
    USUARIO_CIERRE = #{closeUserCode,jdbcType=VARCHAR},

where UID_ACTIVIDAD = #{activityId,jdbcType=VARCHAR}
  and UID_DIARIO_CAJA = #{cashJournalUid,jdbcType=VARCHAR}
  and FECHA_CIERRE IS NULL
