mapper.select:
select

from CONFIG_CONTADORES_RANGOS_TBL
where ID_CONTADOR = #{idContador,jdbcType=VARCHAR}
and ID_RANGO = #{idRango,jdbcType=VARCHAR}

mapper.delete:
delete from CONFIG_CONTADORES_RANGOS_TBL
where ID_CONTADOR = #{idContador,jdbcType=VARCHAR}
and ID_RANGO = #{idRango,jdbcType=VARCHAR}
