mapper.select:
select 

from D_TIPOS_DOCUMENTOS_TBL
where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and ID_TIPO_DOCUMENTO = #{idTipoDocumento,jdbcType=DECIMAL}

mapper.delete:
delete from D_TIPOS_DOCUMENTOS_TBL
where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and ID_TIPO_DOCUMENTO = #{idTipoDocumento,jdbcType=DECIMAL}
