mapper.select:
select 

from D_CAJA_TRANSACCIONES_DET_TBL
where UID_ACTIVIDAD = #{activityId,jdbcType=VARCHAR}
  and UID_TRANSACCION_DET = #{payTransactionLineUid,jdbcType=VARCHAR}

select 

from D_CAJA_TRANSACCIONES_DET_TBL
where UID_ACTIVIDAD = #{activityId,jdbcType=VARCHAR}
  and UID_TRANSACCION = #{payTransactionUid,jdbcType=VARCHAR}
ORDER BY ORDEN

mapper.update:
update D_CAJA_TRANSACCIONES_DET_TBL
set 
  RESPUESTA_TRANSACCION = #{paymentGatewayResponse,jdbcType=VARCHAR},
  FECHA_FIN_TRANSACCION = #{endDate,jdbcType=TIMESTAMP},
  ESTADO = #{newStatusId,jdbcType=DECIMAL}
where UID_ACTIVIDAD = #{activityId,jdbcType=VARCHAR}
  and UID_TRANSACCION_DET = #{payTransactionLineUid,jdbcType=VARCHAR}
  and ESTADO = #{expectedStatusId,jdbcType=DECIMAL}

mapper.select:
select count(*) from D_CAJA_TRANSACCIONES_DET_TBL
where UID_ACTIVIDAD = #{activityId,jdbcType=VARCHAR}
  and UID_TRANSACCION = #{payTransactionUid,jdbcType=VARCHAR}
