mapper.select:
select 

from d_caja_cab_tbl
where uid_actividad = #{uidActividad,jdbcType=VARCHAR}
  and uid_diario_caja = #{uidDiarioCaja,jdbcType=VARCHAR}

mapper.delete:
delete from d_caja_cab_tbl
where uid_actividad = #{uidActividad,jdbcType=VARCHAR}
  and uid_diario_caja = #{uidDiarioCaja,jdbcType=VARCHAR}

mapper.update:
update D_CAJA_CAB_TBL

    FECHA_CIERRE = #{fechaCierre,jdbcType=TIMESTAMP},
    USUARIO_CIERRE = #{usuarioCierre,jdbcType=VARCHAR},

where UID_ACTIVIDAD = #{uidActividad,jdbcType=VARCHAR}
  and UID_DIARIO_CAJA = #{uidDiarioCaja,jdbcType=VARCHAR}
